mapper.select:
SELECT , overdraft_enabled
FROM free_accounts
WHERE member_id = #{memberId}

SELECT , maturity_date, interest_rate
FROM savings_accounts
WHERE member_id = #{memberId}

SELECT SUM(balance) AS totalBalance
FROM (
    SELECT balance FROM free_accounts WHERE member_id = #{memberId}
    UNION ALL
    SELECT balance FROM savings_accounts WHERE member_id = #{memberId}
) AS account_balances
